    private void crearCursoBotonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_crearCursoBotonActionPerformed
        // TODO add your handling code here:
        String nombre = this.nombreTextField.getText();
        String sec = this.seccionTextField.getText();
        char seccion = sec.charAt(0);
        getControlador().crearCurso(nombre, seccion);
        //Validar que la creacion del curso se realizo correctamente
        getControlador().mostrarVentana(TipoVentana.Ventana.INICIO);
    }//GEN-LAST:event_crearCursoBotonActionPerformed

